/// \file manyears_config.hpp Helper functions for ManyEars config file parsing.
#ifndef MANYEARS_CONFIG_HPP
#define MANYEARS_CONFIG_HPP

#include <string>
#include "overallContext.h"

namespace manyears_ros
{
    /// \brief Parse a ManyEars config file.
    /// 
    /// The config file can be generated by the GUI.
    ///
    /// \param context A reference to the ManyEars library context to fill
    ///                parameters in.
    /// \param fn      Config filename (full path).
    /// \return        False if an error occured.
    bool parseConfigFile(objOverall& context, const std::string& fn);

    /// \brief Load a single parameter key/value pair in a ManyEars context.
    ///
    /// \param context ManyEars library context reference.
    /// \param key     Parameter key string.
    /// \param float   Parameter value.
    void loadParameter(objOverall& context, const std::string& key, float val);

}

#endif

